mapper.select:
select
  JS_BankNumber, JS_BankType, JS_BankPassWord, JS_ClientID, JS_BankRemainingSum, JS_BankState
from JSBanKUserInfo.JsClientBank


        and JS_BankNumber = #{jsBanknumber}


        and JS_BankType = #{jsBanktype}


        and JS_BankPassWord = #{jsBankpassword}


        and JS_ClientID = #{jsClientid}


        and JS_BankRemainingSum = #{jsBankremainingsum}


        and JS_BankState = #{jsBankstate}
