mapper.select:
select
  JS_ClientID, JS_ClientName, JS_Sex, JS_CretificateType, JS_IDNumber, JS_PhoneNumber, JS_ClientRank, JS_RegisterWay, JS_InternetOpenType, JS_Nationality, JS_DateOfBirth, JS_MaritalStatus, JS_DegreeOfEducation, JS_Profession, JS_IndustryInvolved, JS_TechnicalTitle, JS_AdministrativeRanks, JS_ToSupportThePopulation, JS_WorkUnit, JS_EnterTheWorkUnitTime
from JSBanKUserInfo.JsClientInfo


        and JS_ClientID = #{jsClientid}


        and JS_ClientName = #{jsClientname}


        and JS_Sex = #{jsSex}


        and JS_CretificateType = #{jsCretificatetype}


        and JS_IDNumber = #{jsIdnumber}


        and JS_PhoneNumber = #{jsPhonenumber}


        and JS_ClientRank = #{jsClientrank}


        and JS_RegisterWay = #{jsRegisterway}


        and JS_InternetOpenType = #{jsInternetopentype}


        and JS_Nationality = #{jsNationality}


        and JS_DateOfBirth = #{jsDateofbirth}


        and JS_MaritalStatus = #{jsMaritalstatus}


        and JS_DegreeOfEducation = #{jsDegreeofeducation}


        and JS_Profession = #{jsProfession}


        and JS_IndustryInvolved = #{jsIndustryinvolved}


        and JS_TechnicalTitle = #{jsTechnicaltitle}


        and JS_AdministrativeRanks = #{jsAdministrativeranks}


        and JS_ToSupportThePopulation = #{jsTosupportthepopulation}


        and JS_WorkUnit = #{jsWorkunit}


        and JS_EnterTheWorkUnitTime = #{jsEntertheworkunittime}
